document.addEventListener("DOMContentLoaded",function(){const panel=document.getElementById("oes-admin-toggle-panel");const toggle=document.getElementById("oes-admin-toggle-toggle");if(!panel)return;const close=panel.querySelector(".oes-admin-toggle-close");toggle.addEventListener("click",()=>{panel.classList.add("open");toggle.style.display="none"});close.addEventListener("click",()=>{panel.classList.remove("open");toggle.style.display="block"})});function oesRemoveElementById(id,confirmMessage=null){if(confirmMessage&&!confirm(confirmMessage))return;const el=document.getElementById(id);if(el)el.remove()}function oesGoToAdminPage(select){const url=select.value;if(url){window.location.href=url}}